    /**
     * 随机生成一个人名
     * @return
     */
    public String getRName() {
        try {
            int index = RandomUtils.nextInt(0, lastNameMap.size());
            String lastName = lastNameMap.get(index);
            String firstName = getFirstName(2);

            return lastName + firstName;
        } catch (Exception e) {
            e.printStackTrace();
        }

        return "";
    }

