    public String getUserName(String name) {
        return Pinyin4JUtil.getPinYin(name);
    }

    /**
     * 随机生成日期
     * @param start yyyy-MM-dd
     * @param end yyyy-MM-dd
     * @return
     */
    public Date getRDate(String start, String end) {
        try {
            SimpleDateFormat sdf = new SimpleDateFormat("yyyy-mm-dd");
            Date startDate = sdf.parse(start);
            Date endDate = sdf.parse(end);

            sdf.applyPattern("yyyy");
            Integer rYear = RandomUtils.nextInt(Integer.parseInt(sdf.format(startDate)),
                    Integer.parseInt(sdf.format(endDate)) + 1);
            sdf.applyPattern("MM");
            Integer rMonth = RandomUtils.nextInt(Integer.parseInt(sdf.format(startDate)),
                    Integer.parseInt(sdf.format(endDate)) + 1);
            sdf.applyPattern("dd");
            Integer rDay = RandomUtils.nextInt(Integer.parseInt(sdf.format(startDate)),
                    Integer.parseInt(sdf.format(endDate)) + 1);

            String yyyyMMdd = rYear + "-" + rMonth + "-" + rDay;
            sdf.applyPattern("yyyy-MM-dd");

            return sdf.parse(yyyyMMdd);

        } catch (ParseException e) {
            e.printStackTrace();
        }

        return new Date();
    }

    /**
     * 根据用户名随机生成邮箱
     * @return
     */
    public String getREmail(String name) {
        Integer index = RandomUtils.nextInt(0, emailSuffixArray.length);
        return getUserName(name) + emailSuffixArray[index];
    }

